const TodoItem = ({ list, onDel, onMod }) => {
  const { id, text, isChk } = list;
  return (
    // <li className={isChk ? "on" : ""}>
    <li style={isChk ? { TextDecoder: "line-throuth" } : ""}>
      <em onClick={() => onMod(id)}>{text}</em>
      <button onClick={() => onDel(id)}>삭제</button>
    </li>
  );
};

export default TodoItem;
